Template Code custom_card_mpse_wifisignal.yaml<pre><code>---
custom_card_mpse_wifisignal:
  template:
    - "icon_info_bg"
  icon: |
    [[[
      var icon = "mdi:wifi-strength-off";
      var signal = states[entity.entity_id].state;
      if (signal &gt;= -50) {
        icon = "mdi:wifi-strength-4";
